import React, { useEffect, useState } from 'react'
import Header from '../../Components/Header/Header.js'
import { createLogin } from '../../Components/Requisitions/Requisitions.js'
import axios from 'axios'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min'
import { goToDetailTripPage } from '../../Routes/Walker'
import { Grid } from '@material-ui/core'
import Button from '@material-ui/core/Button';



const useStyles = makeStyles({
    root: {           
      minWidth: 275,
      maxWidth: 300,
      marginTop: 20,
      
      
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 20,
    },
    body: {
      marginTop: 12,
      marginBottom: 12,
    },
  });

function DetailTripPage() {
    const [tripDetail, setTripDetail] = useState([])


    useEffect((res) => {
        getTripDetails()
    }, [])

    const getTripDetails = () => {
        // console.log("cheguei no detalhe")
        // const userId = localStorage.getItem("id")
        const userToken = localStorage.getItem("token")
        const tripId = localStorage.getItem("tripId")
        // console.log("id:", userId)
        // console.log("token:", userToken)
        const headers = {
            headers: {
                auth: userToken
            }
        }
        axios.get(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/douglas-florido/trip/${tripId}`, headers)
            .then((res) => {
                // console.log(res)
                setTripDetail(res.data.trip.candidates)
            })
            .catch((err) => { console.log(err) })
    }

    return (

        <div>
            
            <Header />
            {
                  tripDetail.map((candidate) => {
                    return (
                      <Grid item>
                          <Card className={classes.root}>
                          <CardContent></CardContent>
                        <h2>{candidate.name}</h2>
                        <p>{candidate.age}</p> <p>Profession: {candidate.profession}</p><p>{candidate.country}</p>
                        <div>{candidate.applicationText}</div>
                        
                        </Card>
                      </Grid>
                      
                    )
                  })}
        </div>

    );
}

export default DetailTripPage;
