import React, { useEffect, useState } from 'react'
import { retrieveTripData } from '../Requisitions/Requisitions'
import Header from '../Header/Header.js'
import axios from 'axios'
import { goToDetailTripPage } from '../../Routes/Walker'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min'
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    maxWidth: 300,
    
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 20,
  },
  pos: {
    marginTop: 12,
    marginBottom: 12,
  },
});

function ListTrips() {
  const classes = useStyles();
  const [trips, setTrips] = useState([])
  const [tripDetail, setTripDetail] = useState([])
  const history = useHistory()

  useEffect(() => {
    allTrips()
  }, [])

  const allTrips = () => {
    axios.get('https://us-central1-labenu-apis.cloudfunctions.net/labeX/douglas-florido/trips')
      .then((res) => {
        // console.log(res)
        // console.log(res.data.trips)
        setTrips(res.data.trips)
      })
      .catch((err) => { console.log(err) })
  }

  


  if (trips !== []) {
    return (
      <>
        {trips.map((trip) => {
          return (
            <Card className={classes.root}>
              <CardContent>
              <Typography className={classes.title} >{trip.name}</Typography >
              <Typography className={classes.pos}>{trip.description}</Typography>
              <p>Planet: {trip.planet}</p> <p>Duration in days: {trip.durationInDays}</p> <p>Date: {trip.date}</p>
              <button onClick={() => goToDetailTripPage(history, trip.id)}>Candidates</button>

              

              </CardContent>
            </Card>
          )
        })
        }
      </>
    )
  }


}

export default ListTrips;
