import React, { useEffect, useState } from 'react'
import { retrieveTripData } from '../Requisitions/Requisitions'
import Header from '../Header/Header.js'
import axios from 'axios'
import { goToDetailTripPage } from '../../Routes/Walker'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min'
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

function ListTrips() {
  const [trips, setTrips] = useState([])
  const [tripDetail, setTripDetail] = useState([])
  const history = useHistory()

  useEffect(() => {
    allTrips()
  }, [])

  const allTrips = () => {
    axios.get('https://us-central1-labenu-apis.cloudfunctions.net/labeX/douglas-florido/trips')
      .then((res) => {
        // console.log(res)
        // console.log(res.data.trips)
        setTrips(res.data.trips)
      })
      .catch((err) => { console.log(err) })
  }

  


  if (trips !== []) {
    return (
      <>
        {trips.map((trip) => {
          return (
            <Card >
              <p>Name: {trip.name}</p>
              <p>Description: {trip.description}</p>
              <p>Planet: {trip.planet}</p> <p>Duration in days: {trip.durationInDays}</p> <p>Date: {trip.date}</p>
              <button onClick={() => goToDetailTripPage(history, trip.id)}>Candidates</button>

              

              <hr />
            </Card>
          )
        })
        }
      </>
    )
  }


}

export default ListTrips;
