import React from "react";
import logo from './logo.svg';
import axios from "axios";
import './App.css';
import { Registrar } from "./Componentes/Registrar";
import { Listar } from "./Componentes/Listar";

class App extends React.Component{

  state = {
    usuarios: [],
    usuarioValue: "",
    emailValue: "",
    pagina: true  
  };


  onChangeInputNome = (event) => {
    this.setState({usuarioValue: event.target.value})
  }

  onChangeInputEmail = (event) => {
    this.setState({emailValue: event.target.value})
  }

  criarUsuario = () =>{
    const body = {
      name: this.state.usuarioValue,
      email: this.state.emailValue
    }

    axios.post("https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users", body, 
    {headers: {
      Authorization:"douglas-florido-epps"
    }}).then((res) => {
      alert("Usuário criado com sucesso!!")
      this.setState({inputName: '', inputEmail: ''})
    })
    .catch((err) => {
      alert("Erro: usuário não pode ser criado :(")
    })
  }


  componentDidMount = () => {    
    this.getAllUsers();    
  };

  mudarPagina = () =>{
    this.setState({pagina: !this.state.pagina})
  }

  render(){



  return (
    <div>
      {this.state.pagina ? <Registrar /> : <Listar />}
      <button onClick={this.mudarPagina}>Mudar de Página</button>
    </div>
  );
  }
}

export default App;
