import React from "react";
import logo from './logo.svg';
import axios from "axios";
import './App.css';

class App extends React.Component{

  state = {
    usuarios: [],
    usuarioValue: "",
    emailValue: ""  
  };

  getAllUsers = async () => {
    try {
      const response = await axios.get("https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users", {
        headers: {
          Authorization:"douglas-florido-epps"
        }
      })
      console.log(response.data)
      this.setState({usuarios: response.data})
    } catch(error) {
      console.log(error)
    }
  }

  onChangeInputNome = (event) => {
    this.setState({usuarioValue: event.target.value})
  }

  onChangeInputEmail = (event) => {
    this.setState({emailValue: event.target.value})
  }

  criarUsuario = () =>{
    const body = {
      name: this.state.usuarioValue,
      email: this.state.emailValue
    }

    axios.post("", body, 
    {headers: {
      Authorization:"douglas-florido-epps"
    }}).then((res) => {
      alert("Usuário criado com sucesso!!")
      this.setState({inputName: '', inputEmail: ''})
    })
    .catch((err) => {
      alert("Erro: usuário não pode ser criado :(")
    })
  }


  componentDidMount = () => {    
    this.getAllUsers();    
  };

  render(){



  return (
    <div>
      <h2>Adicione um novo usuario</h2>
      <input placeholder="Nome" value={this.state.usuarioValue} onChange={this.onChangeInputNome}></input>
      <input placeholder="E-mail" value={this.state.emailValue} onChange={this.onChangeInputEmail}></input>
      <button onClick={this.criarUsuario()}>Salvar</button>
      <h2>Lista de usuarios</h2>
      {this.state.usuarios.map((user) => {
          return (
            <div>
              <p>{user.name}</p>
              <button onClick={() => {this.deleteUser(user.id)}}>X</button>
            </div>
          )
        })}
    </div>
  );
  }
}

export default App;
