import logo from './logo.svg';
import './App.css';
import React from "react"
import axios from "axios"
import {poke} from './Páginas/poke.js'
import {bored} from './Páginas/bored.js'
import {spacex} from './Páginas/spacex.js'


class App extends React.Component  {
  state = {
    valor: 0
  }  

  mudarPagina = () =>{
    if(this.state.valor === 0){
    this.setState({valor: 1})
    }
    else{
      this.setState({valor: 0})
    }
  }


  render(){
    valorBored=1

  return (
    <div>
    <h1>
      PokeBoredSpaceX
    </h1>

    <button onClick={this.mudarPagina(valorBored)}>Bored</button>
    <button>Poke</button>
    <button>SpaceX</button>
    </div>
  );
  }
}

export default App;
