        pokeFoto: ''
    }

    listarPoke = () =>{
     axios.get('https://pokeapi.co/api/v2/pokemon/')
    .then((res) => {
        console.log(res)
        this.setState({pokemons: res.data.results})
        
        
    }).catch((err) => {
        console.log(err)
    })
    }

