    componentDidMount = () => {
        this.listarPoke();
    };
    render(){
        
    return(
        <div>
        {this.state.pokemons.map((pk) => {
            return (
                // const foto = this.pegarFotoPoke(pk.name)

                <div>
                    <p>{pk.name}</p>
                    
                                  
                </div>
            )
        })}
        </div>
    )
    }
}


export default Poke;
