import React from "react";
import axios from "axios";
import { render } from "@testing-library/react";

export class CriarPlaylist extends React.Component{
    state = {
        nomePlaylist: ""
    }

    addPlaylist = () =>{
        axios.post(`https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists`)
    }

    onChangePlaylist = (event) =>{
        this.setState=({nomePlaylist: event.target.value})

    }


    render(){
        return(
            <div>
                <input placeholder="Digite o Nome da Playlist" onChange={this.onChangePlaylist}></input><button onClick={this.addPlaylist}>Criar</button>
            </div>
        )
    }
}

export default CriarPlaylist;
