import React from "react";
import axios from "axios";
import AdicionarTrack from './AdicionarTrack.js'


export class Listar extends React.Component {
    state = {
        playlists: [],
        tracks: [],
        idAddTrack: ""
    }

    getAllPlaylists = () => {
        axios.get('https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists', {
            headers: {
                Authorization: 'douglas-florido-epps'
            }
        }).then((res) => {
            // console.log(res)
            this.setState({ playlists: res.data.result.list })



        }).catch((err) => {
            console.log(err)
        })
    }

    getPlaylistTracks = (id) => {
        // console.log(id)
        axios.get(`https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists/${id}/tracks`, {
            headers: {
                Authorization: 'douglas-florido-epps'
            }
        }).then((res) => {
            // console.log(res)
            console.log(res.data.result.tracks)
            this.setState({ tracks: res.data.result.tracks })



        }).catch((err) => {
            console.log(err)
        })

    }

    onChangeSelect = (event) => {
        // console.log(event.target.value)
        this.setState({idAddTrack: event.target.value})
        this.getPlaylistTracks(event.target.value)


    }

    componentDidMount = () => {
        this.getAllPlaylists()
    }

    render() {

        return (
            <div>
                <div>
                    Playlists
                </div>

                <select onChange={this.onChangeSelect}>
                    <option value={""}></option>
                    {this.state.playlists.map((playlist) => {
                        return (
                            <option value={playlist.id}>{playlist.name}</option>
                        )

                    })}

                </select>


                {this.state.tracks.map((track) => {
                    return (
                        <div>
                            <h4>{track.artist}</h4>
                            <h5>{track.name}</h5>
                            <audio controls>
                                <source src={track.url} />
                            </audio>
                            <hr/>

                        </div>


                    )

                })}





                <AdicionarTrack id={this.state}></AdicionarTrack>
            </div>
        )


    }


}

export default Listar;
