import React, { useState, useEffect } from 'react'
import axios from "axios";


function Pokecard(props) { 
    const[pokepoke,setPokepoke] = useState([])
    

    const pegaPokemon = (pokeName) => {

        axios
            .get(`https://pokeapi.co/api/v2/pokemon/${pokeName}`)
            .then(response => {
                // guarda as infos do pokemon no estado
                // this.setState({ pokemon: response.data });
                setPokepoke(response.data)
                console.log(response.data)
            })
            .catch(err => {
                console.log(err);
            });

    }

    useEffect(() => {
        pegaPokemon(props.pokeName)
        
    }, [props.pokeName])

    return (
        <div>
            <div>{pokepoke.name}</div>
            <div>{pokepoke.weight}</div>
            <div>{pokepoke.types[0]}</div>
            <img src={pokepoke.sprites.front_default} alt={pokepoke.name} />
        </div>
    );
}

export default Pokecard;
